import React, { useState } from "react";
import Register from "./auth/Register";
import Login from "./auth/Login"; // ✅ Using Login as a modal
import "./Header.css";

const Header = () => {
  const [openRegister, setOpenRegister] = useState(false);
  const [openLogin, setOpenLogin] = useState(false);
  const [isLoggedIn, setIsLoggedIn] = useState(!!localStorage.getItem("token")); // ✅ Check localStorage for token

  const handleLoginSuccess = (token) => {
    localStorage.setItem("token", token); // ✅ Save token
    setIsLoggedIn(true); // ✅ Update login state
    setOpenLogin(false); // ✅ Close login modal after successful login
  };

  const handleLogout = () => {
    localStorage.removeItem("token"); // ✅ Remove token
    setIsLoggedIn(false); // ✅ Reset login state
  };

  return (
    <header className="tinted-header">
      <h1 className="header-title">Welcome To ArtFusion</h1>
      <div className="button-container">
        {!isLoggedIn ? (
          <>
            <button className="auth-button" onClick={() => setOpenRegister(true)}>Register</button>
            <button className="auth-button" onClick={() => setOpenLogin(true)}>Login</button>
          </>
        ) : (
        <button className="auth-button" onClick={handleLogout}>Logout</button>

        )}
      </div>

      {/* Signup Dialog */}
      <Register open={openRegister} onClose={() => setOpenRegister(false)} />

      {/* ✅ Login Dialog (Only inside Header) */}
      <Login open={openLogin} onClose={() => setOpenLogin(false)} onLogin={handleLoginSuccess} />
    </header>
  );
};

export default Header;
