// AdminArtworks.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const AdminArtworks = () => {
  const [artworks, setArtworks] = useState([]);

  useEffect(() => {
    // Get the JWT token from localStorage or cookies
    const token = localStorage.getItem('jwtToken');

    // Fetch artworks with the JWT token in the Authorization header
    axios.get('http://localhost:8080/api/admin/dashboard/artworks', {
      headers: {
        'Authorization': `Bearer ${token}`,
      }
    })
      .then(response => {
        setArtworks(response.data);
      })
      .catch(error => {
        console.error('Error fetching artworks:', error);
      });
  }, []);

  const handleDelete = (artworkId) => {
    // Get the JWT token from localStorage or cookies
    const token = localStorage.getItem('jwtToken');

    // Send DELETE request with the JWT token in the Authorization header
    axios.delete(`http://localhost:8080/api/admin/dashboard/artworks/${artworkId}`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      }
    })
      .then(() => {
        // Update the state to remove the deleted artwork
        setArtworks(prev => prev.filter(art => art.id !== artworkId));
      })
      .catch(error => {
        console.error('Error deleting artwork:', error);
      });
  };

  return (
    <div>
      <h2>All Artworks</h2>
      <ul>
        {artworks.map(art => (
          <li key={art.id}>
            <strong>{art.title}</strong> - {art.artistName}
            <button onClick={() => handleDelete(art.id)}>Delete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminArtworks;
