import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";  // âœ… React Router v5
import { jwtDecode } from "jwt-decode";

const Dashboard = () => {
  const [user, setUser] = useState(null);
  const [profile, setProfile] = useState(null); // âœ… Store profile data separately
  const history = useHistory();  

  useEffect(() => {
    const token = localStorage.getItem("token");

    if (!token) {
      history.push("/login");  // âœ… Redirect if no token
      return;
    }

    try {
      const decodedToken = jwtDecode(token); // âœ… Decode token
      setUser(decodedToken);  // âœ… Set user details from token
    } catch (error) {
      console.error("Invalid token:", error);
      localStorage.removeItem("token");
      history.push("/login");
    }
  }, [history]);

  // ðŸ”¹ Fetch profile only when needed (not on every render)
  const fetchProfile = async () => {
    const token = localStorage.getItem("token");
    if (!token) return;

    try {
      const response = await axios.get("http://localhost:8080/api/users/profile", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(response.data);
    } catch (error) {
      console.error("âŒ Failed to fetch profile:", error);
    }
  };

  // ðŸ”¹ Logout and Redirect
  const handleLogout = () => {
    localStorage.removeItem("token");
    history.push("/login"); 
  };

  return (
    <div>
      <h2>Welcome, {user ? user.name : "User"}!</h2>
      <p>Username: {user ? user.username : "N/A"}</p>
      <p>Role: {user ? user.role : "N/A"}</p>

      {/* âœ… Load profile only when needed */}
      <button onClick={fetchProfile}>Load Profile</button>

      {/* âœ… Show profile details only if available */}
      {profile && (
        <div>
          <h3>Profile Information</h3>
          <p>Email: {profile.email}</p>
          <p>Registered Since: {profile.createdAt}</p>
        </div>
      )}

      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default Dashboard;
