import React, { useState } from "react";
import { 
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, MenuItem, IconButton, Snackbar, Alert 
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // ✅ Import useNavigate

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://localhost:8080/api/users/register";

const Register = ({ open, onClose }) => {
  const navigate = useNavigate(); // ✅ Initialize navigate
  const initialFormState = {
    username: "",
    password: "",
    role: "BUYER",
  };

  const [formData, setFormData] = useState(initialFormState);
  const [loading, setLoading] = useState(false);
  const [message, setMessage] = useState({ text: "", type: "success" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  // Handle input changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Validate email format
  const validateEmail = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    // Client-side validation
    if (!formData.username || !formData.email || !formData.password) {
      setMessage({ text: "❌ All fields are required!", type: "error" });
      setSnackbarOpen(true);
      setLoading(false);
      return;
    }
    // if (!validateEmail(formData.email)) {
    //   setMessage({ text: "❌ Invalid email format!", type: "error" });
    //   setSnackbarOpen(true);
    //   setLoading(false);
    //   return;
    // }

    try {
      await axios.post(`${API_BASE_URL}/users/register`, formData);
      setMessage({ text: "✅ Registration successful! Redirecting to login...", type: "success" });
      setSnackbarOpen(true);
      setTimeout(() => {
        navigate("/login"); // ✅ Redirect to login page after success
      }, 2000);
    } catch (error) {
      console.error("Registration error:", error);
      setMessage({ 
        text: error.response?.data?.message || "❌ Registration failed. Try again.", 
        type: "error" 
      });
      setSnackbarOpen(true);
    } finally {
      setLoading(false);
    }
  };

  // Reset form & close dialog
  const handleClose = () => {
    setFormData(initialFormState);
    setMessage({ text: "", type: "success" });
    onClose();
  };

  return (
    <Dialog open={open} onClose={handleClose}>
      <DialogTitle>
        Register
        <IconButton
          aria-label="close"
          onClick={handleClose}
          sx={{ position: "absolute", right: 8, top: 8, color: (theme) => theme.palette.grey[500] }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField label="Username" name="username" fullWidth margin="dense" value={formData.username} onChange={handleChange} />
        <TextField label="Password" name="password" type="password" fullWidth margin="dense" value={formData.password} onChange={handleChange} />
        <TextField select label="Role" name="role" value={formData.role} fullWidth margin="dense" onChange={handleChange}>
          <MenuItem value="BUYER">Buyer</MenuItem>
          <MenuItem value="SELLER">Seller</MenuItem>
          <MenuItem value="ADMIN">Admin</MenuItem>
        </TextField>
      </DialogContent>
      <DialogActions>
        <Button onClick={handleClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained" disabled={loading}>
          {loading ? "Registering..." : "Sign Up"}
        </Button>
      </DialogActions>

      {/* Snackbar for feedback */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Register;
