import React, { useState } from "react";
import Register from "./auth/Register";
import Login from "./auth/Login"; // ✅ Using Login as a modal
import "./Header.css";

const Header = () => {
  const [openRegister, setOpenRegister] = useState(false);
  const [openLogin, setOpenLogin] = useState(false);
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  // const handleLogin = (token) => {
  //   console.log("✅ User logged in with token:", token);
  //   setIsAuthenticated(true); // You can store the token in localStorage if needed
  // };
  const handleLogin = (token, role) => {
    console.log("✅ User logged in with token:", token);
    localStorage.setItem("token", token);
    localStorage.setItem("role", role); // ✅ Store role
    setIsAuthenticated(true);
    setUserRole(role); 
  
    if (userRole === "artist") {
      history.push("/artist-dashboard"); // ✅ Redirect artists
    } else {
      history.push("/dashboard"); // ✅ Redirect other users
    }
    };


//   return (
//     <header className="tinted-header">
//       <h1 className="header-title">Welcome To ArtFusion</h1>
//       <div className="button-container">
//         <button className="auth-button" onClick={() => setOpenRegister(true)}>Register</button>
//         <button className="auth-button" onClick={() => setOpenLogin(true)}>Login</button>
//       </div>

//       {/* Signup Dialog */}
//       <Register open={openRegister} onClose={() => setOpenRegister(false)} />

//       {/* ✅ Login Dialog (Pass `onLogin`) */}
//       <Login open={openLogin} onClose={() => setOpenLogin(false)} onLogin={handleLogin} />
//     </header>
//   );
// };
return (
  <header className="tinted-header">
    <h1 className="header-title">Welcome To ArtFusion</h1>
    <div className="button-container">
      {isAuthenticated ? (
        <>
          <button
            className="auth-button"
            onClick={() => history.push(userRole === "artist" ? "/artist-dashboard" : "/dashboard")}
          >
            Dashboard
          </button>
          <button className="auth-button logout" onClick={handleLogout}>
            Logout
          </button>
        </>
      ) : (
        <>
          <button className="auth-button" onClick={() => setOpenRegister(true)}>
            Register
          </button>
          <button className="auth-button" onClick={() => setOpenLogin(true)}>
            Login
          </button>
        </>
      )}
    </div>

    <Register open={openRegister} onClose={() => setOpenRegister(false)} />
    <Login open={openLogin} onClose={() => setOpenLogin(false)} onLogin={handleLogin} />
  </header>
);
};

export default Header;
