import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    imageUrl: "",
    price: "",
  });

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");

      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      await axios.post("http://localhost:8080/api/artworks/upload", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      alert("‚úÖ Artwork uploaded successfully!");
      setIsDialogOpen(false);
      fetchArtworks(); // Refresh artworks list
    } catch (error) {
      console.error("üî¥ Error uploading artwork:", error);
    }
  };

  return (
    <div style={{ background: "white", minHeight: "100vh", padding: "20px" }}>
      {/* Profile Icon and Dropdown */}
      <div
  style={{
    position: "absolute",
    top: "50px", // Adjust as needed
    right: "20px",
    background: "white",
    borderRadius: "5px",
    boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
    zIndex: 1000, // Ensures it's above other elements
  }}>
        <button
          style={{
            backgroundColor: "transparent",
            border: "none",
            cursor: "pointer",
            fontSize: "20px",
          }}
          onClick={() => setShowProfileDropdown(!showProfileDropdown)}
        >
          <span role="img" aria-label="Profile">üßë</span>
        </button>

        {showProfileDropdown && (
          <div
            style={{
              position: "absolute",
              top: "30px",
              right: "0",
              background: "white",
              boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
              borderRadius: "5px",
              padding: "10px",
              width: "120px",
              textAlign: "center",
            }}
          >
            <p style={{ margin: "5px 0", cursor: "pointer" }}>Profile</p>
            <p style={{ margin: "5px 0", cursor: "pointer" }}>Logout</p>
          </div>
        )}
      </div>

      {/* "Sell Your Art" Button */}
      <button
        style={{
          position: "absolute",
          top: "20px",
          right: "20px",
          backgroundColor: "#007bff",
          color: "white",
          padding: "10px 15px",
          borderRadius: "5px",
          border: "none",
          cursor: "pointer",
        }}
        onClick={() => setIsDialogOpen(true)}
      >
        Sell Your Art
      </button>

      <h1>Welcome to Art Gallery!</h1>
      {artworks.length === 0 ? (
        <p>Loading artworks...</p>
      ) : (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px" }}>
          {artworks.map((art) => (
            <div key={art.id} style={{
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "8px",
              textAlign: "center",
              boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            }}>
              <h3>{art.title}</h3>
              <img src={art.imageUrl} alt={art.title} width="200px" style={{ borderRadius: "8px" }} />
              <p><strong>Category:</strong> {art.category}</p>
              <p><strong>Description:</strong> {art.description}</p>
              <p><strong>Price:</strong> ‚Çπ{art.price}</p>
            </div>
          ))}
        </div>
      )}

      {/* Medium-Sized Dialog Box */}
      {isDialogOpen && (
        <div
          style={{
            position: "fixed",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: "400px",
            background: "white",
            padding: "20px",
            borderRadius: "10px",
            boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
          }}
        >
          <h2>Sell Your Art</h2>
          <form onSubmit={handleSubmit} style={{ display: "flex", flexDirection: "column", gap: "10px" }}>
            <input type="text" name="title" placeholder="Title" value={formData.title} onChange={handleInputChange} required />
            <input type="text" name="category" placeholder="Category" value={formData.category} onChange={handleInputChange} required />
            <textarea name="description" placeholder="Description" value={formData.description} onChange={handleInputChange} required />
            <input type="text" name="imageUrl" placeholder="Image URL" value={formData.imageUrl} onChange={handleInputChange} required />
            <input type="number" name="price" placeholder="Price" value={formData.price} onChange={handleInputChange} required />
            
            <div style={{ display: "flex", justifyContent: "space-between" }}>
              <button type="submit" style={{ background: "#28a745", color: "white", padding: "8px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                Upload
              </button>
              <button type="button" onClick={() => setIsDialogOpen(false)} style={{ background: "#dc3545", color: "white", padding: "8px", borderRadius: "5px", border: "none", cursor: "pointer" }}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
