import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js';
import axios from 'axios';

const CARD_ELEMENT_OPTIONS = {
  style: {
    base: {
      fontSize: '16px',
      color: '#32325d',
      '::placeholder': {
        color: '#a0aec0',
      },
    },
    invalid: {
      color: '#e53e3e',
    },
  },
};

const OrderDetails = () => {
  const { orderId } = useParams();
  const stripe = useStripe();
  const elements = useElements();
  const token = localStorage.getItem('token');

  const [payment, setPayment] = useState(null);
  const [clientSecret, setClientSecret] = useState('');
  const [loading, setLoading] = useState(true);
  const [paymentStatus, setPaymentStatus] = useState('');
  const [error, setError] = useState('');
  const [downloadUrl, setDownloadUrl] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const intentRes = await axios.post(
          'http://localhost:8080/api/payments/create-payment-intent',
          { orderId: Number(orderId) },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setClientSecret(intentRes.data.clientSecret);

        const paymentRes = await axios.get(
          `http://localhost:8080/api/payments/${orderId}`,
          { headers: { Authorization: `Bearer ${token}` } }
        );

        setPayment(paymentRes.data);
        setLoading(false);
      } catch (err) {
        console.error('Error loading payment details:', err.response?.data || err.message);
        setError('Something went wrong while fetching payment details.');
        setLoading(false);
      }
    };

    fetchData();
  }, [orderId, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!stripe || !elements || !clientSecret) return;

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      console.error(result.error.message);
      setError(result.error.message);
    } else {
      const status = result.paymentIntent.status;
      const mappedStatus = status === 'succeeded' ? 'COMPLETED' : 'FAILED';

      try {
        await axios.post(
          'http://localhost:8080/api/payments/confirm',
          {
            transactionId: result.paymentIntent.id,
            status: mappedStatus,
          },
          { headers: { Authorization: `Bearer ${token}` } }
        );

        if (mappedStatus === 'COMPLETED') {
          setPaymentStatus('Payment successful!');
        } else {
          setPaymentStatus('Payment failed. Try again.');
        }
      } catch (err) {
        console.error('Error confirming payment:', err.response?.data || err.message);
        setError('Failed to confirm payment.');
      }
    }
  };

  useEffect(() => {
    const fetchDownloadUrl = async () => {
      if (!payment?.artworkId) {
        console.error('Missing artworkId');
        return;
      }

      console.log('Token:', token);
      console.log('Artwork ID:', payment.artworkId);

      try {
        const res = await axios.get(
          `http://localhost:8080/api/artworks/download/${payment.artworkId}`,
          {
            headers: { Authorization: `Bearer ${token}` },
          }
        );

        if (res.data.downloadUrl) {
          setDownloadUrl(res.data.downloadUrl);
        } else {
          setError('Download URL not found.');
        }
      } catch (err) {
        console.error('Error fetching download URL:', err.response?.data || err.message);
        setError(err.response?.data?.message || 'Unable to fetch download link.');
      }
    };

    if (paymentStatus === 'Payment successful!' && payment) {
      fetchDownloadUrl();
    }
  }, [paymentStatus, payment]);

  if (loading) return <p className="text-center text-white mt-10">Loading...</p>;
  if (error) return <p className="text-center text-red-500 mt-10">{error}</p>;

  return (
    <div
      className="flex justify-center items-center min-h-screen px-4"
      style={{
        padding: '20px',
        backgroundImage: `url("/artist.png")`,
        backgroundSize: 'cover',
        backgroundPosition: 'center',
      }}
    >
      <div className="w-full max-w-md bg-black bg-opacity-80 rounded-2xl shadow-xl p-8">
        <h2 className="text-white text-2xl mb-4 text-center">Order Payment</h2>

        <div className="text-white mb-4 space-y-2">
          <p>
            <strong>Transaction ID:</strong> {payment.transactionId}
          </p>
          <p>
            <strong>Amount:</strong> â‚¹{payment.amount}
          </p>
        </div>

        <form onSubmit={handleSubmit}>
          <div className="p-4 border border-gray-300 rounded-md mb-4 bg-white">
            <CardElement options={CARD_ELEMENT_OPTIONS} />
          </div>

          <button
            type="submit"
            disabled={!stripe || !elements}
            className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
          >
            Pay Now
          </button>
        </form>

        {paymentStatus && (
          <p className="mt-4 text-center font-semibold" style={{ color: '#22c55e' }}>
            {paymentStatus}
          </p>
        )}

        {paymentStatus === 'Payment successful!' && downloadUrl && (
          <div className="text-center mt-6">
            <a
              href={downloadUrl}
              download
              className="bg-green-600 hover:bg-green-700 text-white font-bold py-2 px-4 rounded inline-block"
            >
              Download Artwork
            </a>
          </div>
        )}
      </div>
    </div>
  );
};

export default OrderDetails;
