import React, { useState, useEffect } from "react";
import "./ArtistDashboard.css"; // ✅ Import CSS for styling

const ArtistDashboard = () => {
  const [artistName, setArtistName] = useState("");

  useEffect(() => {
    // ✅ Get the artist's name from localStorage (or replace with API call if needed)
    const storedName = localStorage.getItem("artistName") || "Artist"; 
    setArtistName(storedName);
  }, []);

  return (
    <div className="artist-dashboard">
      <h1 className="welcome-text">Welcome {artistName} to ArtFusion!</h1>
      <h2 className="artist-quote">An artist is not paid for his labour but for his vision...</h2>
    </div>
  );
};

export default ArtistDashboard;
