import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";

const Profile = () => {
  const history = useHistory();

  const [profileImage, setProfileImage] = useState(null);
  const [email, setEmail] = useState("");
  const [phone, setPhone] = useState("");
  const [bio, setBio] = useState("");
  const [address, setAddress] = useState("");
  const [ratings, setRatings] = useState("");
  const [saved, setSaved] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(URL.createObjectURL(file));
    }
  };

  const handleSave = async (e) => {
    e.preventDefault();

    if (email && phone && bio && address && ratings && profileImage) {
      const artistData = {
        email,
        phone,
        bio,
        address,
        ratings,
        // NOTE: If your backend accepts a string URL for the image, you can include this:
        profileImage
      };

      try {
        const token = localStorage.getItem("token"); // Optional: if using JWT

        await axios.post("/api/artists/create", artistData, {
          headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${token}`, // Include this if secured
          },
        });

        setSaved(true);
        alert("Artist profile created successfully!");
      } catch (error) {
        console.error("Error creating profile:", error);
        alert("Failed to create profile. Please try again.");
      }
    } else {
      alert("Please fill all fields including image.");
    }
  };

  return (
    <div
      style={{
        display: "flex",
        minHeight: "100vh",
        backgroundColor: "#f9f9f9",
        padding: "20px",
      }}
    >
      {/* Display saved profile card */}
      {saved && (
        <div
          style={{
            width: "300px",
            backgroundColor: "#fff",
            borderRadius: "10px",
            padding: "20px",
            boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
            marginRight: "30px",
            textAlign: "center",
          }}
        >
          <img
            src={profileImage}
            alt="Profile"
            style={{
              width: "100px",
              height: "100px",
              borderRadius: "50%",
              objectFit: "cover",
              marginBottom: "15px",
            }}
          />
          <h3>{email}</h3>
          <p><strong>Phone:</strong> {phone}</p>
          <p><strong>Bio:</strong> {bio}</p>
          <p><strong>Address:</strong> {address}</p>
          <p><strong>Ratings:</strong> {ratings} / 5</p>
        </div>
      )}

      {/* Form to create/edit profile */}
      <form
        onSubmit={handleSave}
        style={{
          flex: 1,
          backgroundColor: "white",
          borderRadius: "10px",
          padding: "30px",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <h2 style={{ marginBottom: "20px" }}>Edit Artist Profile</h2>

        <label>Upload Profile Image:</label>
        <input
          type="file"
          accept="image/*"
          onChange={handleImageChange}
          style={inputStyle}
        />

        {profileImage && (
          <img
            src={profileImage}
            alt="Preview"
            style={{
              width: "80px",
              height: "80px",
              borderRadius: "50%",
              marginBottom: "20px",
              objectFit: "cover",
            }}
          />
        )}

        <label>Email:</label>
        <input
          type="email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          style={inputStyle}
        />

        <label>Phone Number:</label>
        <input
          type="tel"
          value={phone}
          onChange={(e) => setPhone(e.target.value)}
          style={inputStyle}
        />

        <label>Bio:</label>
        <textarea
          value={bio}
          onChange={(e) => setBio(e.target.value)}
          style={{ ...inputStyle, height: "80px" }}
        />

        <label>Address:</label>
        <input
          type="text"
          value={address}
          onChange={(e) => setAddress(e.target.value)}
          style={inputStyle}
        />

        <label>Ratings (1 to 5):</label>
        <input
          type="number"
          min="1"
          max="5"
          value={ratings}
          onChange={(e) => setRatings(e.target.value)}
          style={inputStyle}
        />

        <button type="submit" style={buttonStyle}>
          Save Profile
        </button>

        <button
          type="button"
          onClick={() => history.push("/artist-dashboard")}
          style={{ ...buttonStyle, backgroundColor: "#ccc", marginLeft: "10px" }}
        >
          Back to Dashboard
        </button>
      </form>
    </div>
  );
};

// Styles
const inputStyle = {
  width: "100%",
  padding: "10px",
  marginBottom: "20px",
  borderRadius: "5px",
  border: "1px solid #ccc",
};

const buttonStyle = {
  padding: "10px 20px",
  backgroundColor: "#007bff",
  color: "white",
  border: "none",
  borderRadius: "5px",
  cursor: "pointer",
};

export default Profile;
