import React, { useEffect, useState } from "react";
import axios from "axios";

const PurchasedArtwork = () => {
  const [purchasedArtworks, setPurchasedArtworks] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchPurchasedArtworks = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get(
          "http://localhost:8080/api/orders/buyer/purchased-artworks",
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        setPurchasedArtworks(response.data);
      } catch (err) {
        console.error(err);
        setError("Failed to fetch purchased artworks.");
      }
    };

    fetchPurchasedArtworks();
  }, []);

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <h1 className="text-3xl font-bold mb-6 text-center text-green-700">
        My Purchased Artworks
      </h1>

      {error && (
        <div className="text-red-500 font-semibold text-center mb-4">{error}</div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {purchasedArtworks.length === 0 && !error ? (
          <p className="text-center col-span-full text-gray-600">
            You haven't purchased any artworks yet.
          </p>
        ) : (
          purchasedArtworks.map((art) => (
            <div
              key={art.artworkId}
              className="bg-white shadow-md rounded-lg overflow-hidden p-4"
            >
              <img
                src={art.imageUrl}
                alt={art.title}
                className="w-full h-60 object-cover mb-4 rounded"
              />
              <div>
                <p><strong>Artwork ID:</strong> {art.artworkId}</p>
                <p><strong>Title:</strong> {art.title}</p>
                <p><strong>Purchase Date:</strong> {new Date(art.purchaseDate).toLocaleString()}</p>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default PurchasedArtwork;
