import React from 'react';
import { BsPersonCircle } from 'react-icons/bs';
import './AdminDashboard.css';

function Header() {
  const titleStyle = {
    fontFamily: "'Bubblegum Sans', cursive",
    fontSize: '28px',
    color: 'white',
    margin: 0,
    top:'10px',
  };

  const profileIconStyle = {
    fontSize: '30px',
    color: 'white',
    cursor: 'pointer',
    position: 'absolute',   // Make it positioned absolutely relative to nearest positioned ancestor
  top: '10px',           // Distance from the top
  right: '10px', 

  };

  return (
    <>
      <link
        href="https://fonts.googleapis.com/css2?family=Bubblegum+Sans&display=swap"
        rel="stylesheet"
      />

      <header className="header">
        <h1 style={titleStyle}>Admin Dashboard</h1>
        <BsPersonCircle style={profileIconStyle} title="Profile" />
      </header>
    </>
  );
}

export default Header;
