import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [formData, setFormData] = useState({
    title: "",
    category: "",
    description: "",
    imageUrl: "",
    price: "",
  });

  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }
      await axios.post("http://localhost:8080/api/artworks/upload", formData, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "multipart/form-data",
        },
        withCredentials: true,
      });
      alert("‚úÖ Artwork uploaded successfully!");
      setIsDialogOpen(false);
      fetchArtworks();
    } catch (error) {
      console.error("üî¥ Error uploading artwork:", error);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url(${process.env.PUBLIC_URL}/room.jpg)` ,
        backgroundSize: "cover",
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        minHeight: "100vh",
        padding: "20px",
        opacity: 0.9,
      }}
    >
      <h1 style={{ color: "white", fontSize: "28px", fontWeight: "bold" }}>
        Welcome to Art Gallery!
      </h1>

      {artworks.length === 0 ? (
        <p>Loading artworks...</p>
      ) : (
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "20px",
          }}
        >
          {artworks.map((art) => (
            <div
              key={art.id}
              style={{
                display: "flex",
                alignItems: "center",
                border: "1px solid #ddd",
                padding: "15px",
                borderRadius: "8px",
                boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                backgroundColor: "white",
                transition: "transform 0.3s",
              }}
            >
              <img
                src={`${art.imageUrl}?timestamp=${new Date().getTime()}`}
                alt="Art"
                style={{
                  width: "150px",
                  height: "150px",
                  borderRadius: "8px",
                  marginRight: "20px",
                }}
              />
              <div style={{ textAlign: "left" }}>
                <h3>{art.title}</h3>
                <p><strong>Category:</strong> {art.category}</p>
                <p><strong>Description:</strong> {art.description}</p>
                <p><strong>Price:</strong> ‚Çπ{art.price}</p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
