import React, { useEffect, useState } from "react";
import axios from "axios";

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [setShowProfileDropdown] = useState(false);
  const [selectedArtwork, setSelectedArtwork] = useState(null); // State for selected artwork
  const [showProfileDropdown] = useState(false);
  useEffect(() => {
    fetchArtworks();
  }, []);

  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("‚ùå No token found. Please log in.");
        return;
      }

      const response = await axios.get("http://localhost:8080/api/artworks/my-artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };
  const handleInputChange = (e) => {
            setFormData({ ...formData, [e.target.name]: e.target.value });
          };
        
          const handleSubmit = async (e) => {
            e.preventDefault();
            try {
              const token = localStorage.getItem("token");
        
              if (!token) {
                console.error("‚ùå No token found. Please log in.");
                return;
              }
        
              await axios.post("http://localhost:8080/api/artworks/upload", formData, {
                headers: {
                  Authorization: `Bearer ${token}`,
                  "Content-Type": "application/json",
                },
              });
        
              alert("‚úÖ Artwork uploaded successfully!");
              setIsDialogOpen(false);
              fetchArtworks(); // Refresh artworks list
            } catch (error) {
              console.error("üî¥ Error uploading artwork:", error);
            }
          };

          return (
            <div
              style={{
                backgroundImage: `url(${process.env.PUBLIC_URL}/midnight_blue_wall_bg.png)`,
                backgroundSize: "cover",
                backgroundPosition: "center",
                minHeight: "100vh",
                padding: "20px",
                opacity: 0.9,
              }}
            >
              {/* Header with Title and Profile Icon */}
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                }}
              >
                <h1 style={{ color: "white", fontSize: "28px", fontWeight: "bold" }}>
                  Welcome to Art Gallery!
                </h1>
                {/* Profile Icon */}
                <div style={{ position: "relative" }}>
                  <button
                    style={{
                      backgroundColor: "transparent",
                      border: "none",
                      cursor: "pointer",
                      fontSize: "20px",
                    }}
                    onClick={() => setShowProfileDropdown(!showProfileDropdown)}
                  >
                    <span role="img" aria-label="Profile">
                      üßë
                    </span>
                  </button>
                  {/* Profile Dropdown */}
                  {showProfileDropdown && (
                    <div
                      style={{
                        position: "absolute",
                        top: "40px",
                        right: "0",
                        background: "white",
                        boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                        borderRadius: "5px",
                        padding: "10px",
                        width: "120px",
                        textAlign: "center",
                      }}
                    >
                      <p style={{ margin: "5px 0", cursor: "pointer" }}>Profile</p>
                      <p style={{ margin: "5px 0", cursor: "pointer" }}>Logout</p>
                    </div>
                  )}
                </div>
              </div>
        
              {/* Sell Your Art Button */}
              <div style={{ marginTop: "20px", textAlign: "center" }}>
                <button
                  onClick={() => setIsDialogOpen(true)}
                  style={{
                    backgroundColor: "#007bff",
                    color: "white",
                    padding: "10px 15px",
                    borderRadius: "5px",
                    border: "none",
                    cursor: "pointer",
                  }}
                >
                  Sell Your Art
                </button>
              </div>
        
              {/* Artworks Grid */}
              <div
                style={{
                  display: "flex",
                  justifyContent: "space-between",
                  alignItems: "center",
                  flexWrap: "wrap",
                  gap: "20px",
                  marginTop: "20px",
                }}
              >
                {artworks.map((art, index) => (
                  <div
                    key={art.id}
                    style={{
                      position: "relative",
                      width: index === artworks.length - 1 ? "280px" : "300px",
                      borderRadius: "10px",
                      overflow: "hidden",
                      cursor: "pointer",
                    }}
                    onClick={() => setSelectedArtwork(art)}
                  >
                    {/* Artwork Image */}
                    <img
                      src={art.imageUrl}
                      alt={art.title}
                      style={{
                        width: "90%",
                        height: "200px",
                        objectFit: "cover",
                        borderRadius: "10px",
                        boxShadow: "0px 4px 15px rgba(255, 215, 0, 0.8)",
                        border: "2px solid gold",
                      }}
                    />
                  </div>
                ))}
              </div>
        
              {/* Sell Your Art Form Modal */}
              {isDialogOpen && (
                <div
                  style={{
                    position: "fixed",
                    top: "50%",
                    left: "50%",
                    transform: "translate(-50%, -50%)",
                    width: "400px",
                    background: "white",
                    padding: "20px",
                    borderRadius: "10px",
                    boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                    zIndex: 1000,
                  }}
                >
                  <h2>Sell Your Art</h2>
                  <form
                    onSubmit={handleSubmit}
                    style={{ display: "flex", flexDirection: "column", gap: "10px" }}
                  >
                    <input
                      type="text"
                      name="title"
                      placeholder="Title"
                      value={formData.title}
                      onChange={handleInputChange}
                      required
                      style={{
                        padding: "8px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                      }}
                    />
                    <input
                      type="text"
                      name="category"
                      placeholder="Category"
                      value={formData.category}
                      onChange={handleInputChange}
                      required
                      style={{
                        padding: "8px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                      }}
                    />
                    <textarea
                      name="description"
                      placeholder="Description"
                      value={formData.description}
                      onChange={handleInputChange}
                      required
                      style={{
                        padding: "8px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                      }}
                    />
                    <input
                      type="text"
                      name="imageUrl"
                      placeholder="Image URL"
                      value={formData.imageUrl}
                      onChange={handleInputChange}
                      required
                      style={{
                        padding: "8px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                      }}
                    />
                    <input
                      type="number"
                      name="price"
                      placeholder="Price"
                      value={formData.price}
                      onChange={handleInputChange}
                      required
                      style={{
                        padding: "8px",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                      }}
                    />
                    <div style={{ display: "flex", justifyContent: "space-between" }}>
                      <button
                        type="submit"
                        style={{
                          background: "#28a745",
                          color: "white",
                          padding: "8px",
                          borderRadius: "5px",
                          border: "none",
                          cursor: "pointer",
                        }}
                      >
                        Upload
                      </button>
                      <button
                        type="button"
                        onClick={() => setIsDialogOpen(false)}
                        style={{
                          background: "#dc3545",
                          color: "white",
                          padding: "8px",
                          borderRadius: "5px",
                          border: "none",
                          cursor: "pointer",
                        }}
                      >
                        Cancel
                      </button>
                    </div>
                  </form>
                </div>
              )}
        
              {/* Pop-up Dialog for Artwork Details */}
              {selectedArtwork && (
                <div
                  style={{
                    position: "fixed",
                    top: "50%",
                    left: "50%",
                    transform: "translate(-50%, -50%)",
                    width: "400px",
                    background: "white",
                    padding: "20px",
                    borderRadius: "10px",
                    boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                    zIndex: 1000,
                  }}
                >
                  <h3>{selectedArtwork.title}</h3>
                  <p>
                    <strong>Category:</strong> {selectedArtwork.category}
                  </p>
                  <p>
                    <strong>Description:</strong> {selectedArtwork.description}
                  </p>
                  <p style={{ fontWeight: "bold", fontSize: "18px" }}>
                    ‚Çπ{selectedArtwork.price}
                  </p>
        
                  <button
                    onClick={() => setSelectedArtwork(null)}
                    style={{
                      background: "#dc3545",
                      color: "white",
                      padding: "8px",
                      borderRadius: "5px",
                      border: "none",
                      cursor: "pointer",
                      marginTop: "10px",
                    }}
                  >
                    Close
                  </button>
                </div>
              )}
            </div>
          );
        };
        
        export default ArtistDashboard;
