  {/* Header and Search */}
  <div style={{display: "flex",flexDirection: "column",gap: "1rem",marginBottom: "2rem"}}>
    <h1  style={{fontSize: "2rem",fontWeight: "bold" }}>Explore Artworks</h1>
    <input
      type="text"
      placeholder="Search by title or artist"
      className="search"
      value={searchTerm}
      onChange={(e) => setSearchTerm(e.target.value)}
    />
  </div>

  {/* Artwork Grid */}
  <div className="art-grid">
    {filteredArtworks.map((artwork) => (
      <div key={artwork.id} style={{
        background: "white",
        color: "black",
        borderRadius: "10px",
        overflow: "hidden",
        boxShadow: "0 4px 15px gold",
        height: "200px",
        width: "300px",
        display: "flex",
        alignItems: "center",
        flexDirection: "column",
        justifyContent: "center"
      }} >
        <img
          src={artwork.imageUrl}
          alt={artwork.title}
          className="card-img"
          onClick={() => handleImageClick(artwork)}
          style={{ cursor: "pointer" }}
        />
      </div>
    ))}

    {filteredArtworks.length === 0 && (
      <p className="no-data">No artworks found.</p>
    )}
  </div>

  {/* Modal */}
  {selectedArtwork && (
    <div className="modal-overlay" onClick={() => setSelectedArtwork(null)}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <img
          src={selectedArtwork.imageUrl}
          alt={selectedArtwork.title}
          className="modal-img"
        />
        <h2 className="modal-title">{selectedArtwork.title}</h2>
        <p className="modal-desc">{selectedArtwork.description}</p>
        <p className="modal-price">Price: ₹{selectedArtwork.price?.toFixed(2)}</p>
        <p className="modal-artist">
          Artist:{" "}
          {selectedArtwork.artist?.username || "Artist info not available"} {/* Show if artist data is missing */}
        </p>
        <button className="btn" onClick={() => handleAddToCart(selectedArtwork)}>
          Add to Cart
        </button>
        <button className="close-btn" onClick={() => setSelectedArtwork(null)}>
          Close
        </button>
      </div>
    </div>
  )}
</div>

);
};

export default UserDashboard;
