import React, { useState } from "react";
import { 
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, IconButton, Snackbar, Alert 
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";
import { useHistory } from "react-router-dom";

const Login = ({ open = true, onClose = () => {}, onLogin }) => { // ✅ Fix props
  const history = useHistory();
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState({ text: "", type: "error" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.username || !formData.password) {
      setMessage({ text: "❌ Username and Password are required!", type: "error" });
      setSnackbarOpen(true);
      return;
    }

    try {
      const response = await axios.post(
      "http://localhost:8080/api/users/login", // ✅ Directly use the backend URL
      formData
    );

      localStorage.setItem("token", response.data.token);
      onLogin(response.data.token);

      setMessage({ text: "✅ Login successful! Redirecting...", type: "success" });
      setSnackbarOpen(true);

      setTimeout(() => {
        history.push("/dashboard");
      }, 1500);
    } catch (error) {
      setMessage({ 
        text: error.response?.data?.message || "❌ Invalid username or password", 
        type: "error" 
      });
      setSnackbarOpen(true);
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>
        Login
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField 
          label="Username" 
          name="username" 
          type="text" 
          fullWidth 
          margin="dense" 
          value={formData.username} 
          onChange={handleChange} 
          required
        />
        <TextField 
          label="Password" 
          name="password" 
          type="password" 
          fullWidth 
          margin="dense" 
          value={formData.password} 
          onChange={handleChange} 
          required
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained">Login</Button>
      </DialogActions>

      {/* Snackbar for Feedback */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Login;
