import React from "react";
import { BrowserRouter as Router, Route, Switch, Redirect } from "react-router-dom";
import Header from "./components/Header";
import BackgroundVideo from "./components/BackgroundVideo";
//import Dashboard from "./components/board/Dashboard";
import ArtistDashboard from "./components/board/ArtistDashboard";
import Login from "./components/auth/Login";
import Register from "./components/auth/Register";

function App() {
  const token = localStorage.getItem("token"); // ðŸ”‘ Check if user is logged in
  const userRole = (localStorage.getItem("role") || "").toLowerCase(); // ðŸŽ­ Get user role

  return (
    <Router>
      <div className="app-container">
        {/* âœ… Show background video only on login/register pages */}
        <Route path={["/login", "/register"]} exact component={BackgroundVideo} />

        <Header /> {/* Always show header */}

        <Switch>
          {/* ðŸ”¹ Default route: If logged in, go to dashboard, else go to login */}
          <Route exact path="/">
            {token ? <Redirect to="/artist-dashboard" /> : <Redirect to="/login" />}
          </Route>

          {/* ðŸ”¹ Authentication routes */}
          <Route exact path="/login" component={Login} />
          <Route exact path="/register" component={Register} />

          {/* ðŸ”¹ Dashboard routes (Only accessible if logged in) */}
          <Route
            exact
            path="/artist-dashboard"
            render={(props) =>
              token ? <ArtistDashboard {...props} /> : <Redirect to="/login" />
            }
          />
          <Route
            exact
            path="/dashboard"
            render={(props) =>
              token && userRole === "artist" ? <ArtistDashboard {...props} /> : <Redirect to="/login" />
            }
          />

          {/* ðŸ”¹ Catch-all for undefined routes */}
          <Route path="*">
            <h2>404 - Page Not Found</h2>
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
