import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CardElement, useStripe, useElements } from "@stripe/react-stripe-js";
import axios from "axios";

const OrderDetails = () => {
  const { orderId } = useParams(); // Route: /orders/:orderId
  console.log("Raw orderId from URL:", orderId); 
  const numericOrderId = Number(orderId);
  const stripe = useStripe();
  const elements = useElements();

  const [order, setOrder] = useState(null);
  const [clientSecret, setClientSecret] = useState("");
  const [loading, setLoading] = useState(true);
  const [paymentStatus, setPaymentStatus] = useState("");
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");
  


  useEffect(() => {
    const fetchPaymentIntentAndOrder = async () => {
      try {
        // 1. Create Payment Intent (this also stores payment in DB)
        const intentRes = await axios.post(
          "http://localhost:8080/api/payments/create-payment-intent",
          { orderId: numericOrderId  },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setClientSecret(intentRes.data.clientSecret);

        // 2. After success, fetch order details
        const orderRes = await axios.get(
          `http://localhost:8080/api/payments/${orderId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setOrder(orderRes.data);
        setLoading(false);
      } catch (err) {
        console.error("Error during payment setup or order fetch:", err);

        if (err.response) {
          if (err.response.status === 403) {
            setError("You are not authorized to view this order.");
          } else if (err.response.status === 500) {
            setError("Something went wrong with payment creation.");
          } else {
            setError("Failed to initiate payment.");
          }
        } else {
          setError("Network or server error occurred.");
        }

        setLoading(false);
      }
    };

    fetchPaymentIntentAndOrder();
  }, [orderId, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) return;

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      setError(result.error.message);
    } else {
      if (result.paymentIntent.status === "succeeded") {
        // Optional: Confirm to backend
        await axios.post(
          "http://localhost:8080/api/payments/confirm",
          {
            transactionId: result.paymentIntent.id,
            status: "SUCCEEDED",
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setPaymentStatus("Payment successful!");
      } else {
        setPaymentStatus("Payment not completed.");
      }
    }
  };

  if (loading) return <p className="text-white text-center mt-10">Loading...</p>;
  if (error) return <p className="text-red-500 text-center mt-10">{error}</p>;

  return (
    <div className="max-w-lg mx-auto mt-10 bg-white p-6 rounded-xl shadow-lg">
      <h2 className="text-2xl font-bold mb-4 text-center">Order Payment</h2>
      <div className="mb-4">
      <p><strong>Artwork ID:</strong> {order.artwork_id}</p>
<p><strong>Buyer ID:</strong> {order.buyer_id}</p>
<p><strong>Price:</strong> â‚¹{order.total_price}</p>

      </div>

      <form onSubmit={handleSubmit}>
        <div className="mb-4 p-4 border rounded">
          <CardElement />
        </div>
        <button
          type="submit"
          disabled={!stripe || !elements}
          className="w-full bg-blue-600 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
        >
          Pay Now
        </button>
      </form>

      {paymentStatus && <p className="mt-4 text-green-600">{paymentStatus}</p>}
    </div>
  );
};

export default OrderDetails;
