import React, { useEffect, useState } from "react";
import axios from "axios";
import { useHistory } from "react-router-dom";
import { FaUserCircle, FaShoppingCart } from "react-icons/fa";
import "./UserDashboard.css";

const UserDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedArtwork, setSelectedArtwork] = useState(null);
  const [artistDetails, setArtistDetails] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const fetchArtworks = async () => {
      try {
        const token = localStorage.getItem("token");
        const response = await axios.get("http://localhost:8080/api/artworks", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setArtworks(response.data);
      } catch (error) {
        console.error("Error fetching artworks:", error);
      }
    };

    fetchArtworks();
  }, []);

  const fetchArtistDetails = async (userId) => {
    try {
      const token = localStorage.getItem("token");
      const response = await axios.get(`http://localhost:8080/api/artists/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtistDetails(response.data);
    } catch (error) {
      console.error("Failed to fetch artist details:", error);
    }
  };

  const handleImageClick = (art) => {
    setSelectedArtwork(art);
    if (art.artist?.id) {
      fetchArtistDetails(art.artist.id);
    }
  };

  const handleAddToCart = async (artwork) => {
    try {
      const token = localStorage.getItem("token");
      await axios.post(
        "http://localhost:8080/api/cart/add",
        { artworkId: artwork.id },
        {
          headers: {
            Authorization: `Bearer ${token}`,
            "Content-Type": "application/json",
          },
        }
      );
      alert("Artwork added to cart!");
      history.push("/cart");
    } catch (error) {
      console.error("Error adding to cart:", error);
      alert("Failed to add to cart.");
    }
  };

  const filteredArtworks = artworks.filter(
    (art) =>
      art.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
      art.artist?.username?.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="page">
      {/* Profile and Cart Icons */}
      <div className="profile-cart-container">
        <div className="tooltip">Fill your profile</div>
        <FaUserCircle size={32} className="icon" onClick={() => history.push("/userprofile")} />
        <div className="tooltip" style={{ marginLeft: "10px" }}>
          View Cart
        </div>
        <FaShoppingCart size={30} className="icon" onClick={() => history.push("/cart")} />
      </div>

      {/* Header and Search */}
      <div className="header">
        <h1 className="title">Explore Artworks</h1>
        <input
          type="text"
          placeholder="Search by title or artist"
          className="search"
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
      </div>

      {/* Artwork Grid */}
      <div className="art-grid">
        {filteredArtworks.map((art) => (
          <div key={art.id} className="card">
            <img
              src={art.imageUrl}
              alt={art.title}
              className="card-img"
              onClick={() => handleImageClick(art)}
              style={{ cursor: "pointer" }}
            />
          </div>
        ))}

        {filteredArtworks.length === 0 && (
          <p className="no-data">No artworks found.</p>
        )}
      </div>

      {/* Modal */}
      {selectedArtwork && (
        <div className="modal-overlay" onClick={() => setSelectedArtwork(null)}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            <img
              src={selectedArtwork.imageUrl}
              alt={selectedArtwork.title}
              className="modal-img"
            />
            <h2 className="modal-title">{selectedArtwork.title}</h2>
            <p className="modal-desc">{selectedArtwork.description}</p>
            <p className="modal-price">Price: ₹{selectedArtwork.price?.toFixed(2)}</p>
            <p className="modal-artist">
              Artist: {artistDetails?.username || selectedArtwork.artist?.username}
            </p>
            <button className="btn" onClick={() => handleAddToCart(selectedArtwork)}>
              Add to Cart
            </button>
            <button className="close-btn" onClick={() => setSelectedArtwork(null)}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default UserDashboard;
