import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // For getting the orderId from the URL
import { useStripe, useElements, CardElement } from '@stripe/react-stripe-js'; // For Stripe Payment
import axios from 'axios'; // For making HTTP requests

const OrderDetails = () => {
  const { orderId } = useParams(); // Route: /orders/:orderId
  const numericOrderId = Number(orderId);
  const stripe = useStripe();
  const elements = useElements();

  const [payment, setPayment] = useState(null);
  const [clientSecret, setClientSecret] = useState("");
  const [loading, setLoading] = useState(true);
  const [paymentStatus, setPaymentStatus] = useState("");
  const [error, setError] = useState("");

  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchPaymentIntentAndDetails = async () => {
      try {
        // 1. Create Payment Intent
        const intentRes = await axios.post(
          "http://localhost:8080/api/payments/create-payment-intent",
          { orderId: numericOrderId },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        setClientSecret(intentRes.data.clientSecret);

        // 2. Fetch Payment Details (includes order & artwork info)
        const paymentRes = await axios.get(
          `http://localhost:8080/api/payments/${orderId}`,
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );
        console.log("Fetched payment data:", paymentRes.data);
        setPayment(paymentRes.data);
        setLoading(false);
      } catch (err) {
        console.error("Error during payment setup or fetch:", err);
        if (err.response) {
          if (err.response.status === 403) {
            setError("You are not authorized to view this order.");
          } else if (err.response.status === 500) {
            setError("Something went wrong with payment creation.");
          } else {
            setError("Failed to initiate payment.");
          }
        } else {
          setError("Network or server error occurred.");
        }

        setLoading(false);
      }
    };

    fetchPaymentIntentAndDetails();
  }, [orderId, token]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!stripe || !elements) return;

    const result = await stripe.confirmCardPayment(clientSecret, {
      payment_method: {
        card: elements.getElement(CardElement),
      },
    });

    if (result.error) {
      setError(result.error.message);
    } else {
      const stripeStatus = result.paymentIntent.status;
      console.log("Stripe status:", stripeStatus);

      let mappedStatus;
      if (stripeStatus === "succeeded") {
        mappedStatus = "COMPLETED";
      } else if (stripeStatus === "processing") {
        mappedStatus = "PENDING";
      } else {
        mappedStatus = "FAILED";
      }

      try {
        await axios.post(
          "http://localhost:8080/api/payments/confirm",
          {
            transactionId: result.paymentIntent.id,
            status: mappedStatus,
          },
          {
            headers: {
              Authorization: `Bearer ${token}`,
            },
          }
        );

        if (mappedStatus === "COMPLETED") {
          setPaymentStatus("Payment successful!");
        } else {
          setPaymentStatus("Payment submitted but not completed.");
        }
      } catch (err) {
        console.error("Error confirming payment:", err);
        setError("Failed to confirm payment to server.");
      }
    }
  };

  if (loading) return <p className="text-white text-center mt-10">Loading...</p>;
