import React, { useEffect, useState } from 'react';
import axios from 'axios';

function OrderStatusPage() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchOrders();
  }, []);

  const fetchOrders = async () => {
    try {
      const token = localStorage.getItem('token'); // Adjust if you're storing token differently
      const response = await axios.get('http://localhost:8080/api/admin/dashboard/orders', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setOrders(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const updateStatus = async (orderId, newStatus) => {
    try {
      const token = localStorage.getItem('token');
      await axios.put(`http://localhost:8080/api/admin/dashboard/orders/${orderId}/status/${newStatus}`, {}, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      fetchOrders(); // Refresh table
    } catch (error) {
      console.error('Failed to update status:', error);
    }
  };

  if (loading) return <p>Loading orders...</p>;

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">Order Status</h2>
      <table className="table-auto w-full border">
        <thead>
          <tr className="bg-gray-200">
            <th className="border px-4 py-2">ID</th>
            <th className="border px-4 py-2">Order Status</th>
            <th className="border px-4 py-2">Order Date</th>
            <th className="border px-4 py-2">Total Price</th>
            <th className="border px-4 py-2">Change Status</th>
          </tr>
        </thead>
        <tbody>
          {orders.map(order => (
            <tr key={order.id}>
              <td className="border px-4 py-2">{order.id}</td>
              <td className="border px-4 py-2">{order.orderStatus}</td>
              <td className="border px-4 py-2">{order.orderDate}</td>
              <td className="border px-4 py-2">â‚¹{order.totalPrice}</td>
              <td className="border px-4 py-2">
                <select
                  onChange={(e) => updateStatus(order.id, e.target.value)}
                  defaultValue=""
                  className="border p-1"
                >
                  <option value="" disabled>Update</option>
                  <option value="PENDING">Pending</option>
                  <option value="SHIPPED">Shipped</option>
                  <option value="DELIVERED">Delivered</option>
                  <option value="CANCELLED">Cancelled</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default OrderStatusPage;
