// export default ArtistDashboard;
import React, { useEffect, useState } from "react";
import axios from "axios";
import AccountCircleIcon from "@mui/icons-material/AccountCircle"; // Import profile icon

const ArtistDashboard = () => {
  const [artworks, setArtworks] = useState([]);
  const [isDialogOpen, setIsDialogOpen] = useState(false);
  const [showProfileDropdown, setShowProfileDropdown] = useState(false);
  const [artistName, setArtistName] = useState("Artist Name"); // Default value

  useEffect(() => {
    fetchArtworks();
    fetchArtistName();
  }, []);

  // Fetch Artworks
  const fetchArtworks = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return console.error("‚ùå No token found. Please log in.");

      const response = await axios.get("http://localhost:8080/api/artworks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setArtworks(response.data);
    } catch (error) {
      console.error("üî¥ Error fetching artworks:", error);
    }
  };

  // Fetch Artist Name
  const fetchArtistName = async () => {
    try {
      const token = localStorage.getItem("token");
      if (!token) return;

      const response = await axios.get("http://localhost:8080/api/artist", {
        headers: { Authorization: `Bearer ${token}` },
      });

      setArtistName(response.data.name); // Assuming API returns { name: "John Doe" }
    } catch (error) {
      console.error("üî¥ Error fetching artist name:", error);
    }
  };

  return (
    <div style={{ background: "white", minHeight: "100vh", padding: "20px" }}>
      {/* Top-right container for Sell Button & Profile Icon */}
      <div style={{ position: "absolute", top: "20px", right: "20px", display: "flex", alignItems: "center", gap: "15px" }}>
        {/* Sell Your Art Button */}
        <button
          style={{
            backgroundColor: "#007bff",
            color: "white",
            padding: "10px 15px",
            borderRadius: "5px",
            border: "none",
            cursor: "pointer",
          }}
          onClick={() => setIsDialogOpen(true)}
        >
          Sell Your Art
        </button>

        {/* Profile Icon */}
        <div style={{ position: "relative" }}>
          <button
            style={{
              backgroundColor: "transparent",
              border: "none",
              cursor: "pointer",
              fontSize: "30px",
            }}
            onClick={() => setShowProfileDropdown(!showProfileDropdown)}
            aria-label="Profile"
          >
            <AccountCircleIcon fontSize="large" />
          </button>

          {/* Dropdown to Show Artist Name */}
          {showProfileDropdown && (
            <div
              style={{
                position: "absolute",
                top: "40px",
                right: "0",
                background: "white",
                padding: "10px",
                borderRadius: "8px",
                boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
                minWidth: "150px",
                textAlign: "center",
              }}
            >
              <p style={{ margin: "0", fontWeight: "bold" }}>{artistName}</p>
            </div>
          )}
        </div>
      </div>

      {/* Main Content */}
      <h1>Welcome to Art Gallery!</h1>

      {/* Artworks Grid */}
      {artworks.length === 0 ? (
        <p>Loading artworks...</p>
      ) : (
        <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)", gap: "20px" }}>
          {artworks.map((art) => (
            <div key={art.id} style={{
              border: "1px solid #ddd",
              padding: "15px",
              borderRadius: "8px",
              textAlign: "center",
              boxShadow: "0px 4px 8px rgba(0,0,0,0.2)",
            }}>
              <h3>{art.title}</h3>
              <img src={art.imageUrl} alt={art.title} width="200px" style={{ borderRadius: "8px" }} />
              <p><strong>Category:</strong> {art.category}</p>
              <p><strong>Description:</strong> {art.description}</p>
              <p><strong>Price:</strong> ‚Çπ{art.price}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default ArtistDashboard;
