import React, { useState } from "react";
import { 
  Dialog, DialogTitle, DialogContent, DialogActions, Button, TextField, IconButton, Snackbar, Alert 
} from "@mui/material";
import CloseIcon from "@mui/icons-material/Close";
import axios from "axios";
import { useNavigate } from "react-router-dom"; // ‚úÖ Updated from useHistory

const Login = ({ open = false, onClose = () => {}, onLogin = () => {} }) => {
  const navigate = useNavigate(); // ‚úÖ Updated from useHistory
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState({ text: "", type: "error" });
  const [snackbarOpen, setSnackbarOpen] = useState(false);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => { 
    e.preventDefault();
    console.log("üîµ Sending Data:", formData);

    if (!formData.username || !formData.password) {
      setMessage({ text: "‚ùå Username and Password are required!", type: "error" });
      setSnackbarOpen(true);
      return;
    }

    try {
      const response = await axios.post("http://localhost:8080/api/users/login", formData, {
        headers: { "Content-Type": "application/json" },
        withCredentials: true, 
      });

      console.log("üü¢ Response Data:", response.data);

      if (response.data.token) {
        localStorage.setItem("token", response.data.token);
        onLogin(response.data.token); // ‚úÖ Ensure onLogin is always a function
        setMessage({ text: "‚úÖ Login successful! Redirecting...", type: "success" });
        setSnackbarOpen(true);

        setTimeout(() => {
          navigate("/dashboard"); // ‚úÖ Replaced history.push with navigate()
        }, 1500);
      } else {
        setMessage({ text: "‚ùå Login failed. No token received.", type: "error" });
        setSnackbarOpen(true);
      }
    } catch (error) {
      console.error("üî¥ Full API Error:", error);

      if (error.response) {
        console.error("üî¥ API Response Data:", error.response.data);
        setMessage({ text: error.response.data?.message || "‚ùå Invalid username or password", type: "error" });
      } else if (error.request) {
        console.error("‚ö†Ô∏è No response received:", error.request);
        setMessage({ text: "‚ö†Ô∏è Server not responding. Try again later.", type: "error" });
      } else {
        console.error("‚ùå Request failed:", error.message);
        setMessage({ text: "‚ùå Network error. Check connection.", type: "error" });
      }

      setSnackbarOpen(true);
    }
  };

  return (
    <Dialog open={open} onClose={onClose}>
      <DialogTitle>
        Login
        <IconButton
          aria-label="close"
          onClick={onClose}
          sx={{ position: "absolute", right: 8, top: 8 }}
        >
          <CloseIcon />
        </IconButton>
      </DialogTitle>
      <DialogContent>
        <TextField 
          label="Username" 
          name="username" 
          type="text" 
          fullWidth 
          margin="dense" 
          value={formData.username} 
          onChange={handleChange} 
          required
        />
        <TextField 
          label="Password" 
          name="password" 
          type="password" 
          fullWidth 
          margin="dense" 
          value={formData.password} 
          onChange={handleChange} 
          required
        />
      </DialogContent>
      <DialogActions>
        <Button onClick={onClose} color="secondary">Cancel</Button>
        <Button onClick={handleSubmit} color="primary" variant="contained">Login</Button>
      </DialogActions>

      {/* Snackbar for Feedback */}
      <Snackbar open={snackbarOpen} autoHideDuration={3000} onClose={() => setSnackbarOpen(false)}>
        <Alert onClose={() => setSnackbarOpen(false)} severity={message.type} sx={{ width: '100%' }}>
          {message.text}
        </Alert>
      </Snackbar>
    </Dialog>
  );
};

export default Login;
