import React, { useState } from "react";
import { useHistory } from "react-router-dom";  // ‚úÖ Correct for React Router v5
import axios from "axios";

const Login = ({ onLogin = () => {} }) => {
  const history = useHistory();  // ‚úÖ React Router v5 History
  const [formData, setFormData] = useState({ username: "", password: "" });
  const [message, setMessage] = useState("");

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post("http://localhost:8080/api/users/login", formData, {
        headers: { "Content-Type": "application/json" },
        withCredentials: true,
      });

      const { token, role } = response.data;

      if (token) {
        localStorage.setItem("token", token);
        localStorage.setItem("username", formData.username);
        localStorage.setItem("role", role);  // ‚úÖ Store role immediately

        // ‚úÖ Redirect based on role immediately after login
        if (role.toLowerCase() === "artist") {
          history.push("/artist-dashboard");  // üéØ Redirect to artist dashboard
        } else {
          history.push("/dashboard");  // üéØ Redirect to normal dashboard
        }
      } else {
        setMessage("‚ùå Login failed. No token received.");
      }
    } catch (error) {
      console.error("üî¥ Login Error:", error);
      setMessage("‚ùå Invalid username or password");
    }
  };

  return (
    <div>
      <h2>Login</h2>
      {message && <p style={{ color: "red" }}>{message}</p>}
      <form onSubmit={handleSubmit}>
        <input type="text" name="username" placeholder="Username" value={formData.username} onChange={handleChange} required />
        <input type="password" name="password" placeholder="Password" value={formData.password} onChange={handleChange} required />
        <button type="submit">Login</button>
      </form>
    </div>
  );
};

export default Login;
