import React, { useEffect, useState } from "react";
import axios from "axios";
import { useParams } from "react-router-dom"; // ⬅️ Get orderId from URL

const OrderDetails = () => {
  const { orderId } = useParams(); // ⬅️ Destructure from route params
  const [order, setOrder] = useState(null);

  useEffect(() => {
    const fetchOrder = async () => {
      try {
        const response = await axios.get(`http://localhost:8080/api/payments/${orderId}`, {
          headers: {
            Authorization: `Bearer ${localStorage.getItem("token")}`,
          },
        });
        setOrder(response.data);
      } catch (error) {
        console.error("Failed to fetch order:", error);
      }
    };

    if (orderId) {
      fetchOrder();
    }
  }, [orderId]);

  if (!order) {
    return <h2 style={{ textAlign: "center", marginTop: "50px" }}>Loading order details...</h2>;
  }

  return (
    <div style={{ padding: "20px" }}>
      <h2>Order Details</h2>
      <div
        style={{
          width: "300px",
          padding: "15px",
          borderRadius: "10px",
          backgroundColor: "#f0f0f0",
          boxShadow: "0 4px 8px rgba(0,0,0,0.1)",
        }}
      >
        <img
          src={order.imageUrl}
          alt={order.artworkTitle}
          style={{ width: "100%", height: "200px", objectFit: "cover", borderRadius: "8px" }}
        />
        <h3>{order.artworkTitle}</h3>
        <p><strong>Artist:</strong> {order.artistName}</p>
        <p><strong>Price:</strong> ₹{order.price}</p>
        <p><strong>Date:</strong> {new Date(order.createdAt).toLocaleDateString()}</p>
        <p><strong>Transaction ID:</strong> {order.transactionId}</p>
        <p><strong>Status:</strong> {order.status}</p>
      </div>
    </div>
  );
};

export default OrderDetails;
